local a=module("vrp","lib/Proxy")vRP=a.getInterface("vRP")RegisterServerEvent('gm_bikerental:tryPayment_vrp')AddEventHandler('gm_bikerental:tryPayment_vrp',function(b)local c=vRP.getUserId({source})local d=false;local e=vRP.getMoney({c})if b.payment>=0 and e>=b.payment then vRP.setMoney({c,e-b.payment})d=true else d=false end;if d==false then local c=vRP.getUserId(source)local e=vRP.getMoney(c)if b.payment>=0 and e>=b.payment then vRP.setMoney(c,e-b.payment)d=true else d=false end end;TriggerClientEvent('gm_bikerental:callback',source,d,b.CallbackID)end)RegisterServerEvent('gm_bikerental:forcePayment_vrp')AddEventHandler('gm_bikerental:forcePayment_vrp',function(b)local c=vRP.getUserId({source})local f=0;local e=vRP.getMoney({c})if b.payment>=0 and e>=b.payment then vRP.setMoney({c,e-b.payment})f=b.payment else f=vRP.getMoney({c})vRP.setMoney({c,0})end;if f==0 then local c=vRP.getUserId(source)local e=vRP.getMoney(c)if b.payment>=0 and e>=b.payment then vRP.setMoney(c,e-b.payment)f=b.payment else f=vRP.getMoney(c)vRP.setMoney(c,0)end end;TriggerClientEvent('gm_bikerental:callback',source,f,b.CallbackID)end)